import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './App.css';

class Superhero extends Component {
    constructor(props) {
        super(props);
        this.state = {
            results: []
        };
    }

    componentDidMount() {
        fetch("http://gateway.marvel.com/v1/public/characters?ts=1&apikey=21277e37e39d6f3f204dfe4fd396bec4&hash=b3632c2406d7b260e92e48799b7e8efb")
          .then(res => res.json())
          .then(
            res => {
                const arr = [];
                for (const key in res.data) {
                    arr.push(res.data[key]);
                }
                this.setState({
                  results: arr[4],
                });
            },
          )
      }


    renderHeroes() {
        const arr2 = [];
        for (const key in this.state.results[0]) {
            arr2.push(this.state.results[0][key]);
        }
        return (
            <div>
            {(() => {
                for (let i in arr2) {
                  this.renderHero();
                  i = i++;
                }
              })()}
            </div>
        )
    }

    renderHero() {
        console.log('hfjk')
        const arr2 = [];
        for (const key in this.state.results[0]) {
            arr2.push(this.state.results[0][key]);
        }
        const thumbnail = [];
        for (const key in arr2[4]) {
            thumbnail.push(arr2[4][key]);
        }
        return (
            <div className="hero-container">
                <img src={thumbnail[0] + "." + thumbnail[1]} alt="thumbnail" />
                <h1>{arr2[1]}</h1>
            </div>
        )
    }

    render() {
          return (
            <div>
                {this.renderHeroes()}
            </div>
          );
    }
}


Superhero.propTypes = {
  results: PropTypes.array
};

export default Superhero;
