            //     )}
            // </div>
        // );
        return <ul>
          {arr2.map(p => <li key={p[0]}>{p[1]}</li>)}
        </ul>;
        // return perso;
    }

    render() {
          return (
            <div>
                {this.renderHero()}
            </div>
          );
    }
}


