import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class Superhero extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: []
        };
    }

    componentDidMount() {
        fetch("http://gateway.marvel.com/v1/public/comics?ts=1&apikey=21277e37e39d6f3f204dfe4fd396bec4&hash=0bddb7177ef79eed2e338bd1245ff4fd")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                items: result.items
              });
            },
          )
      }

    render() {
        const { items } = this.state;
          return (
            <div>
              {
                <h1>{items[0].data.result[1]}</h1>
              }
            </div>
          );
    }
}

export default Superhero;
